def find_total_topics(topics_total):
    list_topics = []
    sub_list = []
    for i in range (-1, len(topics_total) - 1):
        for j in range(len(topics_total[i]) - 1):
            sub_list.append(topics_total[i][j][0])
        list_topics.append(sub_list)
        sub_list = []
    return list_topics


